mapper.select:
SELECT * FROM t_leave l LEFT JOIN t_auditor a ON l.leaveorder = a.leaveorder AND a.statuscode IN ('2','3') where l.personid in (SELECT t.personid from t_personinformation as t left JOIN t_user as u on u.accountname=t.sponsoralarm
where t.sponsoralarm=#{limit} and u.usersystem='1'
where t.policecode=#{limit}
)
and l.personid=#{searchModel.PersonId}
and l.personname like concat('%',#{searchModel.PersonName},'%')
and l.states like concat('%',#{searchModel.condition},'%')
and l.personid=#{searchModel.PersonId} and l.states like concat('%',#{searchModel.condition},'%')
and l.states=#{searchModel.Condition} l.personname like concat('%',#{searchModel.PersonName},'%') and l.subittimestamp > #{searchModel.StartTime} and l.subittimestamp < #{searchModel.EndTime}
and l.personname like concat('%',#{searchModel.PersonName},'%') and l.subittimestamp > #{searchModel.StartTime} and l.subittimestamp < #{searchModel.EndTime}
and l.subittimestamp > TO_TIMESTAMP(#{searchModel.StartTime}, 'YYYY-MM-DD HH24:MI:SS') and l.subittimestamp < TO_TIMESTAMP(#{searchModel.EndTime}, 'YYYY-MM-DD HH24:MI:SS')
and l.subittimestamp > TO_TIMESTAMP(#{searchModel.StartTime}, 'YYYY-MM-DD HH24:MI:SS')
and l.subittimestamp < TO_TIMESTAMP(#{searchModel.EndTime}, 'YYYY-MM-DD HH24:MI:SS')
and l.id in
cast (#{id} as int)
