mapper.select:
select u.accountname as operator,l.modular,l.action,l.operatingtime  from t_log as l
inner join t_user as u on u.id=l.operator
where u.accountname=#{operator} and modular=#{modular}

SELECT (SELECT count(*) from t_user where phone in ( SELECT contact from t_personinformation where sponsoralarm in (SELECT mjbh from fsga_yw_ryb where bmbm like concat('%',#{Areacode},'%'))))as Totalnumber, ( SELECT count(distinct operator) from t_log where operator in ( SELECT "id" from t_user where phone in ( SELECT contact from t_personinformation where sponsoralarm in (SELECT mjbh from fsga_yw_ryb where bmbm like concat('%',#{Areacode},'%'))))
and to_date(to_char(operatingtime,'yyyy-MM-dd'),'yyyy-MM-dd')<=to_date(#{Days},'yyyy-MM-dd')
) as Usernumber
