mapper.select:
SELECT * FROM "t_user"

        where aliasname=#{usermodel.userName}


        and phone=#{usermodel.phone}


        where phone=#{usermodel.phone}



            and status=true


            and status=false




            where status=true


            where status=false




        and createtime >= #{usermodel.startTime}


        where createtime >= #{usermodel.startTime}


        and createtime <= #{usermodel.endTime}


        where createtime <= #{usermodel.endTime}

mapper.update:
UPDATE t_user set accountname=#{user.accountname}, password=#{user.password},aliasname=#{user.aliasname},permissionid=#{user.permissionid},status=#{user.status},
createid=#{user.createid},createname=#{user.createname},areacode=#{user.areacode},phone=#{user.phone},usersystem=#{user.usersystem},officephone=#{user.officephone},police=#{user.police},areaname=#{user.areaname},
department=#{user.department},mailbox=#{user.mailbox},sex=#{user.sex},station=#{user.station},bz=#{user.bz},departmentnum=#{user.departmentnum} where id=#{user.id}
