mapper.select:
SELECT * FROM "t_user" left join fsga_yw_ryb on t_user.accountname=fsga_yw_ryb.mjbh

        where t_user.accountname=#{usermodel.userName}


        and t_user.phone=#{usermodel.phone}


        where t_user.phone=#{usermodel.phone}



            and t_user.status=true


            and t_user.status=false




            where t_user.status=true


            where t_user.status=false




        and t_user.createtime >= #{usermodel.startTime}


        where t_user.createtime >= #{usermodel.startTime}


        and t_user.createtime <= #{usermodel.endTime}


        where t_user.createtime <= #{usermodel.endTime}


    where t_user.createid = #{usermodel.id} or t_user.id = #{usermodel.id}


    and t_user.createid = #{usermodel.id} or t_user.id = #{usermodel.id}

mapper.update:
    UPDATE t_user set accountname=#{user.accountname}, aliasname=#{user.aliasname},permissionid=#{user.permissionid},status=#{user.status},
createid=#{user.createid},createname=#{user.createname},areacode=#{user.areacode},phone=#{user.phone},usersystem=#{user.usersystem},officephone=#{user.officephone},police=#{user.police},areaname=#{user.areaname},
department=#{user.department},mailbox=#{user.mailbox},sex=#{user.sex},station=#{user.station},bz=#{user.bz},departmentnum=#{user.departmentnum}, password=#{user.password} where id=#{user.id}
